import React from 'react';
//import { WiCloud,WiDaySnowWind,WiDayFog,WiDaySunny,WiDayRain } from 'weather-icons-react';
//import WeatherIcons from 'react-weatherIcons';
import ReactAnimatedWeather from 'react-animated-weather';

const stateToContinue = weatherState => {
	switch (weatherState) {
		case "CLOUD":
			return "CLOUD";
		case "CLEAR_DAY":
			return "CLEAR_DAY";
		case "CLEAR_NIGHT":
			return "CLEAR_NIGHT"
		case "PARTLY_CLOUDY_DAY":
			return "PARTLY_CLOUDY_DAY"
		case "RAIN":
			return "RAIN"
		case "SLEET":
			return "SLEET"
		default:
			return "CLEAR_DAY";
		
	}
};


const defaults = {
	icon: 'RAIN',
	color: 'goldenrod',
	size: 36,
	animate: true
  };
/*const getWeatherIcon = weatherState => {
	return (<ReactAnimatedWeather
		icon={defaults.icon}
		color={defaults.color}
		size={defaults.size}
		animate={defaults.animate}
	/>);
}*/
const getWeatherIcon = weatherState => {
	return (<ReactAnimatedWeather icon={stateToContinue(weatherState)} size="36" color='goldenrod'/>);
};
const WeatherTemperature = ({tempeture, weatherState}) => (
	<div>
		{getWeatherIcon(weatherState)}
		
		<span>{`${tempeture} CÂ°`}</span>
	</div>
);

export default WeatherTemperature;

	

