import React, {Component}  from 'react';
import Location from './Location';
import WeatherData from './WeatherData';
import {CLEAR_DAY} from '../../constants/Weathers';
import transforWeather from '../../services/transformWeather';

import './style.css';

const location = "Cali,col";
const api_key ="30c7e4e5097e8f650b23c7225eee4c53";
const api_weather = `http://api.openweathermap.org/data/2.5/weather?q=${location}&appid=${api_key}`;

const data1 = {
    tempeture: 20,
    weatherState: CLEAR_DAY,
    humidity: 10,
    wind: '10 m/s',
};

class WeatherLocation extends Component {

    constructor(){
        super();
        this.state = {
            city: 'Caliio!',
            data: data1
        }; 
    }

   

    handleUpdateClick = () => {
        fetch(api_weather).then(data => {
            console.log(data);
            return data.json();
        }).then( weather_data => {
            debugger;
            const data = transforWeather(weather_data);
            this.setState({ data });
            console.log(weather_data);
            
        });
        /*this.setState ({
            data: data2
        });*/
        console.log("actualizado");
        
    }

    render = () => {    
        const { city, data } = this.state;
        return(
        <div className="weatherLocationCont">
            <Location city={this.state.city} />
            <WeatherData data={this.state.data} /> 
            <button  onClick={this.handleUpdateClick}>Actualizar</button>       
        </div>  
        )

    };
}

export default WeatherLocation;
