import React from 'react';
//import { WiCloud,WiDaySnowWind,WiDayFog,WiDaySunny,WiDayRain } from 'weather-icons-react';
//import { WiCloud,WiDaySnowWind,WiDayFog,WiDaySunny,WiDayRain } from './../constants/Weathers';

const stateToIconName = (weatherState) => {
	switch (weatherState) {
		case WiCloud:
			return 'WiCloud';			
		case WiDaySnowWind:
			return 'WiDaySnowWind';
		case WiDayFog:
			return 'WiDayFog'
		case WiDaySunny:
			return 'WiDaySunny'
		case WiDayRain:
			return 'WiDayRain'
		default:
			return 'WiCloud';
	}
};

const getWeatherIcon = weatherState => {
	return (stateToIconName(weatherState))
	//return (<Weathers class={stateToIconName(weatherState)} size={24} color='#000' />)
};

const WeatherTemperature = ({ tempeture, weatherState}) => (
	<div>
		{getWeatherIcon(weatherState)}
		
		<span>{`${tempeture} CÂ°`}</span>
	</div>
);
export default WeatherTemperature;
