import React, {Component}  from 'react';
import Location from './Location';
import transforWeather from '../../services/transformWeather';
import WeatherData from './WeatherData';
import CircularProgress from '@material-ui/core/CircularProgress';
import './style.css';

const location = "Cali,col";
const api_key ="30c7e4e5097e8f650b23c7225eee4c53";
const api_weather = `http://api.openweathermap.org/data/2.5/weather?q=${location}&appid=${api_key}`;


class WeatherLocation extends Component {

    constructor(){
        super();
        this.state = {
            city: 'Caliio!',
            data: null
        }; 
    }    

    /*getTemp = kelvin => {
        return convert(kelvin).from('K').to('C');//.toFixed(2) cuando los valores son muy grandes con esto los recorta
    }
    //el parametro weather no esta haciendo nada nisiquiera esta definidad
    //se quito en transformWeather porque me generaba error
    getWeatherState = weather => {
        debugger;
        return CLEAR_DAY;
    }

    getData = weather_data => {
        const { humidity, temp } = weather_data.main;
        const { speed } = weather_data.wind;
        const weatherState = this.getWeatherState(this.weather);
        const tempeture= this.getTemp(temp);

        const data = {
            humidity,
            tempeture,
            weatherState,
            wind: `${speed} m/s`,
        }
        return data;
    }*/

    handleUpdateClick = () => {
        fetch(api_weather).then(data => {
            console.log(data);
            return data.json();
        }).then( weather_data => {
            //debugger;
            const data = transforWeather(weather_data);
            this.setState({ data });
            console.log(weather_data);
            
        });
        console.log("actualizado");
        
    }

    componentWillMount() {
        this.handleUpdateClick();
    }

    

    render = () => {    
        const { city, data } = this.state;
        const { classes } =this.props;
        return(
        <div className="weatherLocationCont">
            <Location city={city} />
            { data ? <WeatherData data={data} /> : 'Cargando...'}      
            <CircularProgress className={classes.progress} />
        </div>  
        )

    };
}

export default WeatherLocation;
